import React, {Component, createRef} from 'react';
import { feature } from 'topojson-client';
import axios from 'axios';
import { geoKavrayskiy7 } from 'd3-geo-projection';
import { geoGraticule, geoPath } from 'd3-geo';
import { select as d3Select } from 'd3-selection';
import * as d3Scale from "d3-scale";
import { timeFormat as d3TimeFormat } from "d3-time-format";
import { schemeCategory10 } from "d3-scale-chromatic";
import { WORLD_MAP_URL, SATELLITE_POSITION_URL } from "../constants";

const width = 960;
const height = 600;

class WorldMap extends Component {
    constructor(){
        super();
        this.state = {

        }
        this.map = null;
        this.color = d3Scale.scaleOrdinal(schemeCategory10);
        this.refTrack = createRef();
        this.refMap = React.createRef();
    }

    componentDidMount() {
        axios.get(WORLD_MAP_URL)
            .then(res => {
                const { data } = res;
                const land = feature(data, data.objects.countries).features;
                this.generateMap(land);
            })
            .catch(e => console.log('err in fecth world map data ', e))
    }

    generateMap(land){
        const projection = geoKavrayskiy7()
            .scale(170)
            .translate([width / 2, height / 2])
            .precision(.1);

        const graticule = geoGraticule();

        const canvas1 = d3Select(this.refMap.current)
            .attr("width", width)
            .attr("height", height);

        const canvas2 = d3Select(this.refTrack.current)
            .attr("width", width)
            .attr("height", height);

        let context1 = canvas1.node().getContext("2d");
        let context2 = canvas2.node().getContext("2d");

        let path = geoPath()
            .projection(projection)
            .context(context1);

        land.forEach(ele => {
            context1.fillStyle = '#B3DDEF';
            context1.strokeStyle = '#000';
            context1.globalAlpha = 0.7;
            context1.beginPath();
            path(ele);
            context1.fill();
            context1.stroke();

            context1.strokeStyle = 'rgba(220, 220, 220, 0.1)';
            context1.beginPath();
            path(graticule());
            context1.lineWidth = 0.1;
            context1.stroke();

            context1.beginPath();
            context1.lineWidth = 0.5;
            path(graticule.outline());
            context1.stroke();
        })
    }

    render() {
        return (
            <div className="map-box">
                <canvas className="map" ref={this.refMap} />
                <canvas ref={this.refTrack}/>
            </div>
        );
    }
}

export default WorldMap;
